import React, { useContext } from "react";
import { context } from "../ContextFun";
import Todo from "../Todo";
//styles
import "./todolist.css";
function ToDoList() {
  const { todos } = useContext(context);
  return (
    <div className="todo-container">
      <ul className="todo-list">
        {todos.map((item) => (
          <li>
            <span>{item}</span>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ToDoList;
